import React, { useState } from "react";
import Axios from "axios";

const VideoItem = (props) => {

    const [details, setDetails] = useState([]);
    const [selctedId, setSelctedId] = useState();
  
    // useEffect(() =>{
    // const fetchData = async (moduleId) => {
    //    await Axios.get(`https://stgapi.omnicuris.com/api/v3/courses?courseSlug=thyroid-in-pregnancy&moduleId=${moduleId}`, {
    //       headers: {
    //         'hk-access-token': '89e684ac-7ade-4cd8-bbdf-419a92f4cc5f'
    //       }
    //   })
    //   .then(response => {
    //       //setList(response.data.courseDetails.modules);
    //     //   console.log(response.data);
        
    //       arr.push(response.data);
    //       //console.log(props.list);
    //     })
    // }

    // if (props.list.length) {
    //     props.list.map(l => {
    //         fetchData(l.id)
    //     });
    //     setDetails(arr);
    // } 

    //   return ()=> {
    //     //
    //   };
    // },[props.list]);

    //  console.log(details);

    const fetchData = async (moduleId) => {
      await Axios.get(`https://stgapi.omnicuris.com/api/v3/courses?courseSlug=thyroid-in-pregnancy&moduleId=${moduleId}`, {
         headers: {
           'hk-access-token': '89e684ac-7ade-4cd8-bbdf-419a92f4cc5f'
         }
     })
     .then(response => {
         setDetails(response.data); 
       })
   }

    const findChapter = (event) => {
      console.log(event.target.id);
      fetchData(event.target.id);
      setSelctedId(event.target.id);
      // setDetails(arr);
    }

    const chapterList = () => {
      // console.log(details.lessonDetails[0].userChapterDetails);
      console.log(selctedId);
      return details.lessonDetails[0].userChapterDetails.map(c => {
        // console.log(details.lessonDetails[0].userChapterDetails.length);
        return (
          <div className="video-item-list item">
              <div className="content" id ={c.id}>
                <div className="description" style={{fontSize: "light"}} id ={c.id}>{c.title}</div>
                {/* <div className="description" style={{color: "blue"}} id ={l.id}>{l.name}</div> */}
            </div>
          </div>
        )
      });
      
    }
    
    return ( 
        <>
        {props.list.map( l => <div className="video-item item" onClick={findChapter} id ={l.id} key={l.id}>
        
        <div className="content" id ={l.id}>
        <img alt ={l.title} className="ui avatar image" src ={l.moduleExperts[0].profilePic} id ={l.id}/>
            <div className="header" style={{color: "red"}} id ={l.id}>{l.title}</div>
            <div className="description" style={{color: "blue"}} id ={l.id}>{l.name}</div>
            <div className="clock-icon" id ={l.id}><i className="clock outline icon" ></i>{`${l.duration} mins`}</div>
            <div onClick className="ui large relaxed divided list">
              {l.id=== selctedId && console.log(`${selctedId} -- ${l.id} -- ${l.title}`)}
                {details.lessonDetails && chapterList()}
            </div>
              
        </div>
        {/* <div><ul>{details.lessonDetails && chapterList()}</ul></div> */}
        
        </div>
          
        )}
        <ul>{details.lessonDetails && chapterList()}</ul>
    
        
    </>
    );
}

export default VideoItem;
