import React, { useState } from 'react';
// import logo from './logo.svg';
import VideoList from "./components/VideoList"
import { Player } from "video-react";
import "../node_modules/video-react/dist/video-react.css";

import './App.css';
import { Link, BrowserRouter } from 'react-router-dom';

function App() {
  const [videoContent, setvideoContent] = useState({});
  const [videoTitle, setvideoTitle] = useState('');
  const [videoSlug, setvideoSlug] = useState('');

  const chapterClick = (content,title,slug) => {



    setvideoContent({ content : content,title : title ,  slug: slug});
    // setvideoTitle(title);
    // setvideoSlug(slug);
  }
  return (
    <BrowserRouter>
          <div className="grid-container">
      <header className="header">
      <Link to="/" className="header_link">Omnicuris</Link>

      </header>

      <main className="main">
        {/* <div className="ui container"> */}
          <div className="ui grid" style={{margin : "0 2rem"}}>
            <div className="ui row">
              <div className = "eleven wide column">
                <Player
                  playsInline
                  poster="/images/omni.jpg"
                  src={`${videoContent}`}
                />
                {videoTitle && <div> {`Description : ${videoTitle} -- ${videoSlug}`}</div> }

              </div>
              <div className = "five wide column">              
                <VideoList chapterClick={chapterClick}/>              
              </div>
            </div>
          </div>
        {/* </div> */}
      </main>

      <footer className="footer">
        India's Largest Online CME PlatForm
      </footer>


    </div>
    </BrowserRouter>

  );
}

export default App;
