import React, { useState, useEffect } from 'react';
// import logo from './logo.svg';
import VideoList from "./components/VideoList"
import { Player } from "video-react";
import "../node_modules/video-react/dist/video-react.css";

import './App.css';
import { Link, BrowserRouter } from 'react-router-dom';
import Axios from 'axios';

function App() {
  const [videoContent, setvideoContent] = useState({});
  const [expertsList, setExpertList] = useState([]);

  useEffect(() =>{
const fetchData = async () => {
   await Axios.get("https://stgapi.omnicuris.com/api/v3/courses/thyroid-in-pregnancy/experts", {
      headers: {
        'hk-access-token': '89e684ac-7ade-4cd8-bbdf-419a92f4cc5f'
      }
  })
  .then(response => {
      // setList(response.data.courseDetails.modules);
      setExpertList(response.data.expertDetails);
    })
  }
  fetchData();
  return ()=> {
    //
  };
},[]);

  console.log(expertsList);

  const chapterClick = (content,title,slug) => {
    setvideoContent({ content : content,title : title , slug: slug});
  }

  return (
    <BrowserRouter>
          <div className="grid-container">
      <header className="header">
      <Link to="/" className="header_link">Omnicuris</Link>

      </header>

      <main className="main">
        {/* <div className="ui container"> */}
          <div className="ui grid" style={{margin : "0 2rem", height : "50%"}}>
            <div className="ui row" style={{height : "50%"}}>
              <div className = "eleven wide column">
                <Player
                  playsInline
                  poster="/images/omni.jpg"
                  src={`${videoContent.content}`}
                />
                {videoContent.title && <div> {`Description : ${videoContent.title} -- ${videoContent.slug}`}</div> }

              </div>
              <div className = "five wide column">              
                <VideoList chapterClick={chapterClick}/>              
              </div>

            </div>



          </div>
          <div className="ui massive horizontal divided list" style={{height : "50%" , overflow: "scroll"}}>{expertsList.map(l => <div class="item">
                  <img class="ui avatar image" src={l.profilePic} />
                  <div class="content">
                    <div class="header">{l.expertName}</div>
                      Top Contributor
                    </div>
              </div>)}
          </div>
      </main>

      <footer className="footer">
        India's Largest Online CME PlatForm
      </footer>


    </div>
    </BrowserRouter>

  );
}

export default App;
