       await Axios.get(`https://stgapi.omnicuris.com/api/v3/courses?courseSlug=thyroid-in-pregnancy&moduleId=${moduleId}`, {
          headers: {
            'hk-access-token': '89e684ac-7ade-4cd8-bbdf-419a92f4cc5f'
          }
      })
      .then(response => {
