      return ()=> {
        //
      };
    },[props.list]);

    console.log(details);
    return ( 
        <>
        {props.list.map( l => <div className="video-item item">
        <img alt ={l.title} className="ui avatar image" src ={l.moduleExperts[0].profilePic} />
        <div className="content">
            <div className="header" style={{color: "red"}}>{l.title}</div>
            <div className="description" style={{color: "blue"}}>{l.name}</div>
